def get_zodiac_dict() -> dict:
    with open(ZODIAC, "r", encoding="utf-8") as fh:
        dict = json.load(fh)

    return dict

def get_zodiac(sign: str) -> str:
    dict = get_zodiac_dict()
    return dict[sign]

def get_chinese_dict() -> dict:
    with open(CHINESE, "r", encoding="utf-8") as fh:
        dict = json.load(fh)
    
    return dict

def get_chinese(sign: str) -> str:
    dict = get_chinese_dict()
    return dict[sign]

def get_angel_dict() -> dict:
    with open(ANGEL, "r", encoding="utf-8") as fh:
        dict = json.load(fh)
        
    return dict

def get_angel(num: str) -> str:
    dict = get_angel_dict()
    return dict[num]